import { Component, OnInit } from '@angular/core';
import { Nastavnik } from '../modeli/nastavnik';
import { KorisnikService } from '../servisi/korisnik.service';

@Component({
  selector: 'app-nastavnik-profile',
  templateUrl: './nastavnik-profile.component.html',
  styleUrls: ['./nastavnik-profile.component.css']
})
export class NastavnikProfileComponent implements OnInit {
  ulogovan: Nastavnik = new Nastavnik();

  constructor(private korisnikService: KorisnikService){}

  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
    }
  }

  updateProfessor() {
    this.korisnikService.updateProfessor(this.ulogovan).subscribe(response => {
      console.log('Profesor updated successfully', response);
    }, error => {
      console.error('Error updating profesor', error);
    });
  }
}
<h3>Informacije o Nastavniku</h3>
<form (ngSubmit)="updateProfessor()">
  <div>
    <label>Ime:</label>
    <input [(ngModel)]="ulogovan.ime" name="ime" required>
  </div>
  <div>
    <label>Prezime:</label>
    <input [(ngModel)]="ulogovan.prezime" name="prezime" required>
  </div>
  <div>
    <label>Korisnicko ime:</label>
    <input [(ngModel)]="ulogovan.username" name="username" required readonly>
  </div>
  <div>
    <label>Broj telefona:</label>
    <input [(ngModel)]="ulogovan.telefon" name="telefon">
  </div>
  <div>
    <label>Katedra:</label>
    <input [(ngModel)]="ulogovan.katedra" name="katedra">
  </div>
  <div>
    <label>Uloga:</label>
    <input [(ngModel)]="ulogovan.uloga" name="uloga" readonly>
  </div>
  <button type="submit">Sačuvaj</button>
</form>

