import { Component, OnInit } from '@angular/core';
import { DokumentaplacanjaService } from '../servisi/dokumentaplacanja.service';
import { Student } from '../modeli/student';

@Component({
  selector: 'app-student-profile',
  templateUrl: './student-profile.component.html',
  styleUrls: ['./student-profile.component.css']
})
export class StudentProfileComponent implements OnInit {

  ulogovan: Student = new Student();
  poruka:string = '';

  constructor(private dokumentiplacanjaservis: DokumentaplacanjaService){}

  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
    }
  }

  overiSemestar(): void {
    if (this.ulogovan.overenSemestar === 'overen') {
      this.poruka = 'Semestar je već overen.';
      return;
    }

    this.dokumentiplacanjaservis.overiSemestar(this.ulogovan.username).subscribe({
      next: (res) => {
        if (res === 'Uspešno ste overili semestar.') {
          this.poruka = res;

          // Update ulogovan object fields
          this.ulogovan.overenSemestar = 'overen';
          this.ulogovan.racun -= 2000;

          // Save the updated ulogovan object back to localStorage
          localStorage.setItem('ulogovan', JSON.stringify(this.ulogovan));
        } else {
          this.poruka = res; // Handle other success messages if any
        }
      },
      error: (err) => {
        this.poruka = err.error;
      }
    });
  }


}
<h3>Informacije o studentu</h3>
Ime: {{ulogovan.ime}} <br>
Prezime: {{ulogovan.prezime}} <br>
Korisnicko ime: {{ulogovan.username}} <br>
Pol: {{ulogovan.pol === 'M' ? 'Musko' : 'Zensko' }} <br>
Broj telefona {{ulogovan.telefon}} <br>
Godina studija: {{ulogovan.studijskaGodina}} <br>
Smer: {{ulogovan.smer}}
<hr>

<div>
  <h3>Overa semestra</h3>
  <p>Status: <strong>{{ ulogovan.overenSemestar }}</strong></p>
  <button
    class="btn btn-primary"
    [disabled]="ulogovan.overenSemestar === 'overen'"
    (click)="overiSemestar()">
    Overi semestar
  </button>

  <div *ngIf="poruka" class="alert alert-info mt-3">
    {{ poruka }}
  </div>
</div>
