import { Component, Output, EventEmitter, OnInit} from '@angular/core';
import { Student } from '../modeli/student';

@Component({
  selector: 'app-meni',
  templateUrl: './meni.component.html',
  styleUrls: ['./meni.component.css']
})
export class MeniComponent implements OnInit {
  ulogovan: Student = new Student();
  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
    }
  }
  @Output() menuItemSelected = new EventEmitter<string>();

  selectMenuItem(menuItem: string) {
    this.menuItemSelected.emit(menuItem);
  }
}
<div class="sidebar d-flex flex-column justify-content-between">
  <div class="user-info text-center py-3">
    <img src="assets/default-profile.png" alt="Profile Picture" class="profile-picture img-fluid rounded-circle mb-2">
    <div class="user-details">
      <div class="user-name">{{ulogovan.ime}} {{ulogovan.prezime}}</div>
    </div>
  </div>
  <ul class="list-unstyled">
    <li class="py-2 px-3" (click)="selectMenuItem('profile')">Pocetna</li>
    <li class="py-2 px-3" (click)="selectMenuItem('predmeti-dashboard')">Predmeti</li>
    <li class="py-2 px-3" (click)="selectMenuItem('prijave-predmeta')">Biranje predmeta</li>
    <li class="py-2 px-3" (click)="selectMenuItem('placanja')">Placanja</li>
    <li class="py-2 px-3" (click)="selectMenuItem('obavestenja')">Obavestenja</li>
    <li class="py-2 px-3" (click)="selectMenuItem('dokumenti')">Dokumenti</li>
  </ul>
  <app-logout class="logout-button btn btn-secondary m-3"></app-logout>
</div>
