import { Component, OnInit } from '@angular/core';
import { Uplata } from '../modeli/uplata';
import { HttpClient } from '@angular/common/http';
import { DokumentaplacanjaService } from '../servisi/dokumentaplacanja.service';
import { Student } from '../modeli/student';

@Component({
  selector: 'app-student-payments',
  templateUrl: './student-payments.component.html',
  styleUrls: ['./student-payments.component.css']
})
export class StudentPaymentsComponent implements OnInit {

  ulogovan: Student =new Student();
  uplate:Uplata[] = [];

  constructor(private http: HttpClient, private dokumentiplacanjaservis: DokumentaplacanjaService){}

  ngOnInit(): void{
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);

      this.dokumentiplacanjaservis.uplateKorisnika(this.ulogovan.korisnicko_ime).subscribe(data =>{
        this.uplate = data;
      })
    }

  }

}
<h3 class="my-4">Pregled svih uplata</h3>
<table border="1" *ngIf="uplate.length > 0; else nemaUplata" class="table table-bordered table-striped table-hover">
  <thead class="thead-dark">
  <tr>
    <th>Naziv</th>
    <th>Datum uplate</th>
    <th>Iznos</th>
  </tr>
</thead>
  <tr *ngFor="let a of uplate">
    <td class="text-left">NazivUplate</td>
    <td class="text-left">{{a.datum}}</td>
    <td class="text-left">{{a.iznos}}RSD</td>
  </tr>
</table>{{uplate[0].id_uplate}}
<ng-template #nemaUplata>
  Nije bilo uplata. xd
</ng-template>
