import { Component, Output, EventEmitter, OnInit } from '@angular/core';
import { Korisnik } from '../modeli/korisnik';

@Component({
  selector: 'app-admin-meni',
  templateUrl: './admin-meni.component.html',
  styleUrls: ['./admin-meni.component.css']
})
export class AdminMeniComponent implements OnInit{
  ulogovan: Korisnik = new Korisnik();
  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
    }
  }
  @Output() menuItemSelected = new EventEmitter<string>();

  selectMenuItem(menuItem: string) {
    this.menuItemSelected.emit(menuItem);
  }
}
<div class="sidebar d-flex flex-column">
  <div class="user-info text-center py-3">
    <img src="assets/default-profile.png" alt="Profile Picture" class="profile-picture img-fluid rounded-circle mb-2">
    <div class="user-details">
      <div class="user-name">{{ulogovan.ime}} {{ulogovan.prezime}}</div>
    </div>
  </div>
  <ul class="list-unstyled">
    <li class="py-2 px-3" (click)="selectMenuItem('profile')">Pocetna</li>
    <li class="py-2 px-3" (click)="selectMenuItem('administracija-predmeta')">Predmeti</li>
    <li class="py-2 px-3" (click)="selectMenuItem('administracija-studenta')">Studenti</li>
  </ul>
  <app-logout class="logout-button mt-auto m-3"></app-logout>
</div>
