import { Component, OnInit } from '@angular/core';
import { Nastavnik } from '../modeli/nastavnik';
import { KorisnikService } from '../servisi/korisnik.service';

@Component({
  selector: 'app-nastavnik-profile',
  templateUrl: './nastavnik-profile.component.html',
  styleUrls: ['./nastavnik-profile.component.css']
})
export class NastavnikProfileComponent implements OnInit {
  ulogovan: Nastavnik = new Nastavnik();

  constructor(private korisnikService: KorisnikService){}

  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
    }
  }

  onSubmit(): void {
    this.korisnikService.updateProfessor(this.ulogovan).subscribe(
      response => {
        alert('Update successful!');
        localStorage.setItem("ulogovan", JSON.stringify(response));
      },
      error => {
        alert('Update failed!');
        console.error(error);
      }
    );
  }
}
<h3>Informacije o Nastavniku</h3>
<form (ngSubmit)="onSubmit()">
    Ime: <input [(ngModel)]="ulogovan.ime" name="ime" required> <br>
    Prezime: <input [(ngModel)]="ulogovan.prezime" name="prezime" required> <br>
    Korisnicko ime: <input [(ngModel)]="ulogovan.username" name="username" readonly> <br>
    Lozinka: <input [(ngModel)]="ulogovan.lozinka" name="lozinka" required type="password"> <br>
    Broj telefona: <input [(ngModel)]="ulogovan.telefon" name="telefon"> <br>
    Katedra: <input [(ngModel)]="ulogovan.katedra" name="katedra"> <br>
    Uloga: <input [(ngModel)]="ulogovan.uloga" name="uloga" readonly> <br>
    <button type="submit">Update</button>
</form>
