import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { KorisnikService } from '../servisi/korisnik.service';


@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent {
  constructor(private korisnikServis: KorisnikService, private router: Router){}
  username:string='';
  password:string='';
  greska:string='';

  tip:string = 'administrator';

  login(){
    if (this.username == "") {
      this.greska = "Nije uneto korisnicko ime";
    }
    else if (this.password == "") {
      this.greska = "Nije uneta lozinka";
    }
    else {
      this.korisnikServis.prijava(this.username, this.password).subscribe(data => {
          if (data == null) {
            this.greska = 'Takav administrator u bazi ne postoji';
          } else {
            localStorage.setItem('ulogovan', JSON.stringify(data));
            if(data.tip != 'administrator'){
              this.greska = 'Takav administrator u bazi ne postoji';
            }
            this.router.navigate(['/admin'])
          }
        });

  }
  }
}
<app-header></app-header>
<br><br><br><br>
<div class="full-height center-container">

  <div class="card card-custom">
    <h4>ADMIN LOGIN</h4>
    <table class="table table-borderless">
      <tr>
        <td>Korisnicko ime:</td>
        <td><input type="text" [(ngModel)]="username" class="form-control" placeholder="Unesite korisnicko ime"></td>
      </tr>
      <tr>
        <td>Lozinka:</td>
        <td><input type="password" [(ngModel)]="password" class="form-control" placeholder="Unesite lozinku"></td>
      </tr>
      <tr>
        <td><button (click)="login()" type="submit" class="btn btn-light">Prijava</button>
        </td>
      </tr>
    </table>
    <td colspan="2">
      <span style="color: red;">{{greska}}</span>
  </div>
  <br>
</div>
