import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-student-layout',
  templateUrl: './student-layout.component.html',
  styleUrls: ['./student-layout.component.css']
})
export class StudentLayoutComponent {
  constructor(private router: Router) {}

  onMenuItemSelected(menuItem: string) {
    if (menuItem === 'profile') {
      this.router.navigateByUrl('/student');
    } else {
      this.router.navigate(['/student', menuItem]);
    }
  }
  @Output() menuItemSelected = new EventEmitter<string>();
  selectedMenuItem: string = 'profile';

  selectMenuItem(menuItem: string) {
    this.selectedMenuItem = menuItem;
    this.menuItemSelected.emit(menuItem);
  }
  isActive(menuItem: string): boolean {
    return this.selectedMenuItem === menuItem;
  }
}
<app-header></app-header>
    <div class="wrapper">
      <app-meni (menuItemSelected)="onMenuItemSelected($event)"></app-meni>
      <div class="main-content">
        <router-outlet></router-outlet>
      </div>
    </div>
    <div class="sidebar d-flex flex-column p-3 bg-dark text-white">
      <ul class="nav nav-pills flex-column mb-auto">
        <li class="nav-item" (click)="onMenuItemSelected('profile')">
          <a class="nav-link text-white" [class.active]="isActive('profile')" href="javascript:void(0)">Profile</a>
        </li>
        <li class="nav-item" (click)="onMenuItemSelected('courses')">
          <a class="nav-link text-white" [class.active]="isActive('courses')" href="javascript:void(0)">Courses</a>
        </li>
        <li class="nav-item" (click)="onMenuItemSelected('documents')">
          <a class="nav-link text-white" [class.active]="isActive('documents')" href="javascript:void(0)">Documents</a>
        </li>
      </ul>
    </div>
