import { Component, OnInit } from '@angular/core';
import { NastavniksaradniciService } from '../servisi/nastavniksaradnici.service';
import { Predmet } from '../modeli/predmet';
import { Nastavnik } from '../modeli/nastavnik';

@Component({
  selector: 'app-nastavnik-course-administration',
  templateUrl: './nastavnik-course-administration.component.html',
  styleUrls: ['./nastavnik-course-administration.component.css']
})
export class NastavnikCourseAdministrationComponent implements OnInit{
  ulogovan: Nastavnik = new Nastavnik();
  predmeti: Predmet[] = [];
  selectedPredmet: Predmet | null = null;
  isEditing = false;

  constructor(private nastavniksaradniciService: NastavniksaradniciService) {}

  ngOnInit(): void {
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);
      this.loadCourses();
    }
  }

  loadCourses(): void {
    this.nastavniksaradniciService.getCoursesByProfessor(this.ulogovan.username).subscribe(
      (data) => {
        this.predmeti = data;
      },
      (error) => {
        console.error('Error loading courses:', error);
      }
    );
  }

  onCourseSelect(event: Event): void {
    const selectElement = event.target as HTMLSelectElement;
    const courseId = selectElement.value;

    if (courseId) {
      const id = parseInt(courseId, 10);
      this.nastavniksaradniciService.getCourseById(id).subscribe(
        (course) => {
          this.selectedPredmet = course;
        },
        (error) => {
          console.error('Error loading course details:', error);
        }
      );
    }
  }

  toggleEdit(): void {
    this.isEditing = !this.isEditing;
  }

  saveCourse(): void {
    if (this.selectedPredmet) {
      this.nastavniksaradniciService.updateCourse(this.selectedPredmet.idPredmeta, this.selectedPredmet).subscribe(
        (updatedCourse) => {
          this.selectedPredmet = updatedCourse;
          this.isEditing = false;
        },
        (error) => {
          console.error('Error updating course:', error);
        }
      );
    }
  }
}
<div class="container">
  <h2>Course Details</h2>
  <div class="form-group">
    <label for="courseSelect">Select a course:</label>
    <select id="courseSelect" class="form-control" (change)="onCourseSelect($event)">
      <option *ngFor="let predmet of predmeti" [value]="predmet.idPredmeta">{{ predmet.imePredmeta }}</option>
    </select>
  </div>

  <div *ngIf="selectedPredmet">
    <h3>Course Information</h3>
    <div *ngIf="!isEditing">
      <p><strong>Course Name:</strong> {{ selectedPredmet.imePredmeta }}</p>
      <p><strong>Year:</strong> {{ selectedPredmet.godina }}</p>
      <p><strong>Credits:</strong> {{ selectedPredmet.brojKredita }}</p>
      <p><strong>Description:</strong> {{ selectedPredmet.opisPredmeta }}</p>
      <button class="btn btn-primary" (click)="toggleEdit()">Edit</button>
    </div>

    <div *ngIf="isEditing">
      <form (ngSubmit)="saveCourse()">
        <div class="form-group">
          <label for="imePredmeta">Course Name:</label>
          <input id="imePredmeta" [(ngModel)]="selectedPredmet.imePredmeta" name="imePredmeta" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="godina">Year:</label>
          <input id="godina" type="number" [(ngModel)]="selectedPredmet.godina" name="godina" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="brojKredita">Credits:</label>
          <input id="brojKredita" type="number" [(ngModel)]="selectedPredmet.brojKredita" name="brojKredita" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="opisPredmeta">Description:</label>
          <textarea id="opisPredmeta" [(ngModel)]="selectedPredmet.opisPredmeta" name="opisPredmeta" class="form-control" required></textarea>
        </div>
        <button type="submit" class="btn btn-success">Save</button>
        <button type="button" class="btn btn-secondary" (click)="toggleEdit()">Cancel</button>
      </form>
    </div>
  </div>
</div>
