import { Component, OnInit } from '@angular/core';
import { Dokument } from '../modeli/dokument';

import { HttpClient } from '@angular/common/http';
import { DokumentaplacanjaService } from '../servisi/dokumentaplacanja.service';
import { Student } from '../modeli/student';

@Component({
  selector: 'app-student-documents',
  templateUrl: './student-documents.component.html',
  styleUrls: ['./student-documents.component.css']
})
export class StudentDocumentsComponent implements OnInit {

  ulogovan: Student =new Student();
  dokumenta:Dokument[] = [];

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }


}
<h3 class="my-4">Pregled svih uplata</h3>
<table border="1" *ngIf="dokumenta.length > 0; else nemaDokumenata" class="table table-bordered table-striped table-hover">
  <thead class="thead-dark">
  <tr>
    <th>Naziv dokumenta</th>
    <th>Tip dokumenta</th>
    <th>Id Dokumenta</th>
    <th>Preuzimanje</th>
  </tr>
</thead>
  <tr *ngFor="let a of dokumenta">
    <td class="text-left">{{a.naziv_dokumenta}}</td>
    <td class="text-left">{{a.tip_dokumenta}}</td>
    <td class="text-left">{{a.id_dokumenta}}</td>
    <td class="text-left"><button>preuzmui</button></td>
  </tr>
</table>
<hr>
<p>Dodaj Novi Dokument</p>
<button>Upload</button>
<ng-template #nemaDokumenata>
  Nema uploadovanih dokumenata.
</ng-template>
