import { Component, Output, EventEmitter} from '@angular/core';

@Component({
  selector: 'app-meni',
  templateUrl: './meni.component.html',
  styleUrls: ['./meni.component.css']
})
export class MeniComponent {
  @Output() menuItemSelected = new EventEmitter<string>();

  selectMenuItem(menuItem: string) {
    this.menuItemSelected.emit(menuItem);
  }
}
<div class="sidebar">
  <div class="user-info">
    <img src="assets/default-profile.png" alt="Profile Picture" class="profile-picture">
    <div class="user-details">
      <div class="user-name">{{ulogovan.ime}} {{ulogovan.prezime}}</div>
    </div>
  </div>
  <ul>
    <li (click)="selectMenuItem('profile')">Pocetna</li>
    <li (click)="selectMenuItem('predmeti-dashboard')">Predmeti</li>
    <li (click)="selectMenuItem('prijave-predmeta')">Biranje predmeta</li>
    <li (click)="selectMenuItem('placanja')">Placanja</li>
    <li (click)="selectMenuItem('obavestenja')">Obavestenja</li>
    <li (click)="selectMenuItem('dokumenti')">Dokumenti</li>
  </ul>
  <app-logout class="logout-button"></app-logout>
</div>
