import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DokumentaplacanjaService } from '../servisi/dokumentaplacanja.service';
import { Student } from '../modeli/student';
import { Dkmnt } from '../modeli/dkmnt';

@Component({
  selector: 'app-student-documents',
  templateUrl: './student-documents.component.html',
  styleUrls: ['./student-documents.component.css']
})
export class StudentDocumentsComponent implements OnInit {

  ulogovan: Student =new Student();
  dkmnti:Dkmnt[] = [];
  selectedFile: File| null = null;

  constructor(private http: HttpClient, private dokumentiplacanjaservis: DokumentaplacanjaService){}

  ngOnInit(): void{
    let x = localStorage.getItem("ulogovan");
    if (x != null) {
      this.ulogovan = JSON.parse(x);

      this.dokumentiplacanjaservis.dokumentiKorisnika(this.ulogovan.username).subscribe(data =>{
        this.dkmnti = data;
      })
    }
  }
  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  uploadDocument(documentName: String): void {
    if (this.selectedFile && this.ulogovan.username) {
      const uploadData = new FormData();
      const documentName = prompt('Enter document name');
      if (documentName) {
        const fileName = `${this.ulogovan.username}_${documentName}`;
        uploadData.append('file', this.selectedFile, fileName);

        this.dokumentiplacanjaservis.uploadDocument(uploadData).subscribe(response => {
          console.log('Upload response:', response);
        });
      }
    } else {
      alert('No file selected or user not logged in');
    }
  }

}
<h3 class="my-4">Pregled svih dokumenata</h3>
<table border="1" *ngIf="dkmnti.length > 0; else nemaDokumenata" class="table table-bordered table-striped table-hover">
  <thead class="thead-dark">
  <tr>
    <th>Naziv dokumenta</th>
    <th>Tip dokumenta</th>
    <th>Id Dokumenta</th>
    <th>Preuzimanje</th>
  </tr>
</thead>
  <tr *ngFor="let d of dkmnti">
    <td class="text-left">{{d.nazivDokumenta}}</td>
    <td class="text-left">{{d.tipDokumenta}}</td>
    <td class="text-left">{{d.idDokumenta}}</td>
    <td class="text-left">link</td>
  </tr>
</table>
<hr>
<h5>Dodaj Novi Dokument</h5>
<input type="file" (change)="onFileSelected($event)">
<input type="text" [(ngModel)]="documentName" placeholder="Unesi ime dokumenta">
<button (click)="uploadDocument(documentName)">Upload Document</button>
<ng-template #nemaDokumenata>
  Nema uploadovanih dokumenata.
</ng-template>
