import { Component, OnInit } from '@angular/core';
import { Student } from '../modeli/student';
import { Nastavnik } from '../modeli/nastavnik';
import { AdminostaloService } from '../servisi/adminostalo.service';

@Component({
  selector: 'app-admin-korisnici',
  templateUrl: './admin-korisnici.component.html',
  styleUrls: ['./admin-korisnici.component.css']
})
export class AdminKorisniciComponent implements OnInit{

  students: Student[] = [];
  professors: Nastavnik[] = [];
  showStudents: boolean = true; // Toggle state
  selectedUser: Student | Nastavnik | null = null;

  constructor(private adminService: AdminostaloService) {}

  ngOnInit(): void {
    this.fetchStudents();
    this.fetchProfessors();
  }

  // Fetch students
  fetchStudents(): void {
    this.adminService.getAllStudents().subscribe((data) => {
      this.students = data;
    });
  }

  // Fetch professors
  fetchProfessors(): void {
    this.adminService.getAllProfessors().subscribe((data) => {
      this.professors = data;
    });
  }

  // Toggle between students and professors
  toggleView(): void {
    this.showStudents = !this.showStudents;
  }

  // Delete a user
  deleteUser(username: string): void {
    this.adminService.deleteUser(username).subscribe(() => {
      this.showStudents ? this.fetchStudents() : this.fetchProfessors();
    });
  }

  // Add or edit a user
  saveUser(user: Student | Nastavnik): void {
    if (this.showStudents) {
      this.adminService.addStudent(user as Student).subscribe(() => this.fetchStudents());
    } else {
      this.adminService.addProfessor(user as Nastavnik).subscribe(() => this.fetchProfessors());
    }
  }

  // Open user for editing
  editUser(user: Student | Nastavnik): void {
    this.selectedUser = { ...user }; // Clone the user to edit
  }

  // Reset selected user
  cancelEdit(): void {
    this.selectedUser = null;
  }
  // Sorting methods
  sortStudentsBy(field: keyof Student): void {
    this.students.sort((a, b) => {
      const valueA = a[field] ?? '';
      const valueB = b[field] ?? '';
      return String(valueA).localeCompare(String(valueB));
    });
  }

  sortProfessorsBy(field: keyof Nastavnik): void {
    this.professors.sort((a, b) => {
      const valueA = a[field] ?? '';
      const valueB = b[field] ?? '';
      return String(valueA).localeCompare(String(valueB));
    });
  }
  createNewUser(): void {
    if (this.showStudents) {
      this.editUser(new Student());
    } else {
      this.editUser(new Nastavnik());
    }
  }
}
<div class="container mt-4">
  <button class="btn btn-primary mb-3" (click)="toggleView()">
    Show {{ showStudents ? 'Professors' : 'Students' }}
  </button>

  <table class="table table-bordered table-striped" *ngIf="showStudents">
    <thead>
      <tr>
        <th (click)="sortStudentsBy('ime')">First Name</th>
        <th (click)="sortStudentsBy('prezime')">Last Name</th>
        <th (click)="sortStudentsBy('smer')">Major</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let student of students">
        <td>{{ student.ime }}</td>
        <td>{{ student.prezime }}</td>
        <td>{{ student.smer }}</td>
        <td>
          <button class="btn btn-warning btn-sm" (click)="editUser(student)">Edit</button>
          <button class="btn btn-danger btn-sm" (click)="deleteUser(student.username)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>

  <table class="table table-bordered table-striped" *ngIf="!showStudents">
    <thead>
      <tr>
        <th (click)="sortProfessorsBy('ime')">First Name</th>
        <th (click)="sortProfessorsBy('prezime')">Last Name</th>
        <th (click)="sortProfessorsBy('katedra')">Department</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let professor of professors">
        <td>{{ professor.ime }}</td>
        <td>{{ professor.prezime }}</td>
        <td>{{ professor.katedra }}</td>
        <td>
          <button class="btn btn-warning btn-sm" (click)="editUser(professor)">Edit</button>
          <button class="btn btn-danger btn-sm" (click)="deleteUser(professor.username)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>

  <button class="btn btn-success mt-3" (click)="createNewUser()">
    Add New {{ showStudents ? 'Student' : 'Professor' }}
  </button>


  <!-- User Edit Form -->
  <div *ngIf="selectedUser" class="mt-4">
    <h4>{{ selectedUser.username ? 'Edit' : 'Add' }} {{ showStudents ? 'Student' : 'Professor' }}</h4>
    <form (ngSubmit)="saveUser(selectedUser)">
      <input [(ngModel)]="selectedUser.ime" placeholder="First Name" class="form-control mb-2" required />
      <input [(ngModel)]="selectedUser.prezime" placeholder="Last Name" class="form-control mb-2" required />
      <input [(ngModel)]="selectedUser.pol" placeholder="Gender" class="form-control mb-2" />
      <button class="btn btn-primary" type="submit">Save</button>
      <button class="btn btn-secondary" (click)="cancelEdit()" type="button">Cancel</button>
    </form>
  </div>
</div>
